object gen_pict{
  //使用RGB数据组成图片
  /*
   * r_data:Array[Int]  像素的R值,范围在[0,255]之间
   * g_data:Array[Int]  像素的G值,范围在[0,255]之间
   * b_data:Array[Int]  像素的B值,范围在[0,255]之间
   * width:Int,图片的宽
   * height:Int,图片的高,width*height==r_data。length==g_data。length==b_data。length
   * jpg_out_path:String  输出图片地址
   * */
  def gen_pict_fun(r_data:Array[Int],g_data:Array[Int],b_data:Array[Int],width:Int,height:Int,jpg_out_path:String):Int={
    if(width<=0 || height<=0 || jpg_out_path=="" || r_data.length==0 || g_data.length==0 || b_data.length==0){
      return -1
    }
    //设置图像缓冲区
    val bufferedImage:BufferedImage=new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
    var rgb_tmp:Int=0;
    var rgb_index:Int=0;
    //写入rgb数据
    for(row:Int <- 0 until height){
      for(col:Int <- 0 until width){ 
        rgb_index=row*width+col
        //合成rgb数据
        rgb_tmp = ((r_data(rgb_index)<<16)|(g_data(rgb_index)<<8)|b_data(rgb_index))&0xffffff;
        //设置rgb
        bufferedImage.setRGB(col, row,rgb_tmp); 
      }
    }
    //写入图片文件
    val file:File = new File(jpg_out_path);
    ImageIO.write(bufferedImage, "jpg", file); 
    //成功
    1
  }
  
  //仅仅使用一组数据,并设置为RGM的一种 其余=255 组成图片
  /*
   * data_in 像素的值,范围在[0,255]之间
   * use_type_in:=R 则data_in写入R中,G和B均=0
   *             =G 则data_in写入G中,R和B均=0
   *             =B 则data_in写入B中,G和R均=0
   * width:Int,图片的宽
   * height:Int,图片的高,width*height==r_data。length==g_data。length==b_data。length
   * jpg_out_path:String  输出图片地址
   * */
  def gen_pict_use_one_fun(data_in:Array[Int],width:Int,height:Int,jpg_out_path:String,use_type_in:String="R"):Int={
    if(data_in.length>0 && (use_type_in=="R" || use_type_in=="G" || use_type_in=="B")){
      var r_data:Array[Int]=new Array(data_in.length)
      var g_data:Array[Int]=new Array(data_in.length)
      var b_data:Array[Int]=new Array(data_in.length)
      for(i <- 0 until data_in.length){
        r_data(i)=0;
        g_data(i)=0;
        b_data(i)=0;
      }       
      if(use_type_in=="R"){
        r_data=data_in;
      }else if(use_type_in=="G"){
        g_data=data_in;
      }else if(use_type_in=="B"){
        b_data=data_in;
      }
      gen_pict_fun(r_data,g_data,b_data,width,height,jpg_out_path)
      return 1 
    }
    0
  } 
  
